def print_gradients(model, x):
    output = model(x)
    target = torch.tensor([0.])

    loss = nn.MSELoss()
    loss = loss(output, target)
    
    loss.backward()
    for name, param in model.named_parameters():
        if 'weight' in name:
            print(f"{name }: has gradient mean of {param.grad.abs().mean().item()}")
    

